-- Data for table `spirit`
-- -----------------------------------------------------
START TRANSACTION;
USE `sipdb`;
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (1, 3, 'Tito\'s', 'This gluten-free vodka is distilled using old-fashioned copper pot stills, inspired by the distillation methods of fine single malt scotches and high-end French cognacs. Made in batches with each batch taste-tested, it is goes down smooth and has an impeccably clean finish.', NULL, 80, 'United States', 'Texas');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (2, 1, 'Jack Daniel\'s', 'Sour mash whiskey made in Tennessee from natural corn, rye, barley malt and then charcoal filtered. Sweet with caramel, vanilla and charred wood. Pure, clean, and smooth. Classic. Enjoy on the rocks or in mixed drinks.', NULL, 80, 'United States', 'Tennessee');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (3, 2, 'Bulleit', 'Oaky, smoky, and spicy. The dry, clean flavor is mellow and spice with rye, not hot in the throat. This 90-proof Kentucky bourbon delivers a wonderfully complex taste with hints of vanilla and honey and a long smoky finish.', NULL, 90, 'United States', 'Kentucky');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (4, 4, 'Hendrick\'s', 'The original Hendrick\'s Gin, oddly infused with Rose and Cucumber. Not surprisingly, awarded Platinum at LA Spirit Awards and Silver at SF World Sprits Awards in 2020.', NULL, 80, 'Scotland', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (5, 5, 'Captain Morgan', 'Mixes aromas of marshmallow, light toffee and light spiced honey, leading into a molasses-centric flavor. Ideal for spicing up tropical cocktails or mixed with cola.', NULL, 70, 'Jamaica', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (6, 6, 'Don Juilo', 'Don Julio himself has produced premium tequilas for more than 50 years. He still personally oversees the planting and harvesting of the blue agave so only the best are hand-selected and used in his Reposado Tequila.', NULL, 80, 'Mexico', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (7, 7, 'Glenfiddich 21 Year', 'Glenfiddich 21 Year Old Rum Cask Finish Scotch Whisky is luxuriously rich single malt finished in our own unique rum casks for a rich, sweet and exotic flavor. Awarded Gold at ISC and IWSC in 2020.', NULL, 80, 'Scotland', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (8, 3, 'Grey Goose', 'World\'s best tasting vodka. Single origin soft winter wheat from picardi and natural spring water from Gensac la Pallue. Every step of our process is designed to express the extraordinary character of our wheat. Naturally rich and full bodied taste with a smooth mouthfeel.', NULL, 80, 'France', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (9, 3, 'Smirnoff', 'ure, clean and smooth with a pleasing texture and classic aromas. Light charcoal, citrus, flower flavors. Distilled three times from American grains. Great for mixed drinks.', NULL, 80, 'United States', 'Illinois');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (10, 3, 'Absolut', 'Absolut is a Swedish vodka made exclusively from natural ingredients, and doesn\'t contain any added sugar. In fact Absolut is as clean as vodka can be. Rich, full-bodied and complex, yet smooth and mellow with a distinct character of grain, followed by a hint of dried fruit.', NULL, 80, 'Sweden', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (11, 3, 'Kettle One', 'Clear as rainwater. Bouquet doesn\'t reach up and grab you as much as it seduces you with gentle waves of mild spirit and grainy richness. The palate entry offers mild spiciness and cereal sweetness...Classy and complex.', NULL, 80, 'Netherlands', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (12, 4, 'Bombay Sapphire', 'The spirit is triple distilled, and the alcohol vapors are passed through bundles of the herbs and spices in order to gain flavor and aroma of almond, lemon peel, liquorice, juniper berries, orris root, angelica, coriander, cassia, cubeb, and grains of paradise.', NULL, 94, 'England', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (13, 4, 'Gordon\'s', 'Developed using the flavor of handpicked juniper berries blended with botanicals, following a recipe going back nearly 250 years.', NULL, 75, 'Scotland', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (14, 4, 'Tanqueray', 'Crafted using a time honored recipe of only the highest quality grains, the purest water, the most select juniper berries, finest botanicals, and a unique quadruple-distillation process.', NULL, 94.6, 'Scotland', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (15, 4, 'Beefeater', 'Beefeater London Dry has a remarkably clean flavor, with a bold juniper character that is balanced with strong citrus notes, making it the perfect gin to enjoy with the tonic of your choice.', NULL, 80, 'England', 'London');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (16, 5, 'Bacardi', 'Clear, light and smooth with a silky finish and nutty flavors. Bacardi makes this rum using distilled sugar cane molasses. The mixture is then double-filtered through charcoal. Extremely mixable.', NULL, 80, 'Cuba', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (17, 5, 'Malibu', 'Made in the spirit of traditional Caribbean flavored rums and filled with the fresh, natural taste of coconut, this smooth-drinking rum infuses any drink with a taste of the tropics. Perfect on its own or as a refreshing splash in any rum-based cocktail.', NULL, 42, 'Barbados', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (18, 5, 'Don Q', 'Aged for 1-1/2 to 5 years in American white oak barrels to impart a touch more smoothness. So smooth, in fact, that you can substitute Cristal in a vodka drink, and most won\'t be able to tell the difference. Try it. You\'ll see.', NULL, 80, 'United States', 'Puerto Rico');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (19, 5, 'Appleton', 'A variety of rums aged between five and ten years, blended together in oak vats and aged for several months. Aromas of molasses and brown sugar with a hint of maple. Mild sweetness balanced out by citrusy, nutty flavors. Creamy, mellow finish that is dry and resilient.', NULL, 80, 'Jamaica', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (20, 1, 'Maker\'s Mark', 'Smooth and approachable with an easy finish, a true contrast to hot, harsh whiskies that \"blow your ears off\" and a downright revolutionary idea at the time.', NULL, 90, 'United States', 'Kentucky');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (21, 1, 'Jameson', ' A pot still whiskey blended of light and medium flavored whiskies that were triple distilled for exceptional smoothness. Matured in Bourbon and Sherry casks to produce a crisp, clean, mellow taste with sweet, spicy, vanilla tones, and a subtle finish. Very smooth and versatile.', NULL, 80, 'Ireland', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (22, 1, 'Evan Williams', 'All the corn and oak you\'d want, with mint edging around the sides to lift it, but it doesn\'t blow your head up with your Bourbon head and roughness. Heaven Hill gives it a few extra years int he barrel, and it pays off in smoother character.', NULL, 86, 'United States', 'Kentucky');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (23, 1, 'Jim Bean', 'The world\'s number one bourbon. Jim Beam was founded in 1795 and has been operated by one family for seven generations. We\'ve always created bourbon our way, aging it twice as long as the law requires.', NULL, 80, 'United States', 'Kentucky');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (24, 2, 'Buffalo Trace', 'Bourbon Cream is handcrafted using Buffalo Trace Kentucky Straight Bourbon, making it a perfect marriage of rich delicious cream and smooth Kentucky bourbon.You can enjoy it chilled, on the rocks, or pour it in a cup of coffee for an indulgent after dinner treat.', NULL, 90, 'United States', 'Kentucky');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (25, 2, 'Angels Envy', 'Kentucky straight bourbon whiskey finished in a port pipe. This is veteran master distiller Lincoln Henderson\'s newest creation, and it\'s a beauty. Richly textured, silky, and well-rounded, with ripe berried fruits, candied tangerine, light toffee, maple syrup...', NULL, 87, 'United States', 'Kentucky');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (26, 2, 'Knob Creek', 'Undeniably big, full flavor that strikes your senses with a maple sugar aroma, distinctive sweetness and a rich, woody, caramel flavor. Bottled at 100 proof, it\'s aged in the deepest charred American Oak barrels. Make a bolder classic cocktail or sip it neat.', NULL, 90, 'United States', 'Kentucky');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (27, 2, 'Elijah Craig', 'Bottled exclusively from a dumping of 70 barrels or less, all drawn from the middle to upper floors of the traditional metal clad rickhouses. It is the original Small Batch Bourbon, having been made available even before the term was invented.', NULL, 94, 'United States', 'Kentucky');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (28, 6, 'Casamigos', 'Crisp and clear, with subtle hints of vanilla and a smooth finish. It has been distilled in copper-lined stills, then filtered, and aged two months in stainless steel containers. Great served neat or on the rocks, and perfect base for premium cocktails.', NULL, 80, 'Mexico', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (29, 6, 'Epsolon', 'Start each tequila cocktail with Espolon Tequila Blanco, the purest expression of Espolon tequila. Created in the highlands of Jalisco as a tequila for the people, Espolon Tequila Blanco is inspired by and a tribute to Mexican culture. Handcrafted with 100% Blue Weber Agave.', NULL, 80, 'Mexico', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (30, 6, 'Milagro', 'Milagro Silver is a 100% blue agave tequila that is renowned for its crisp, fresh, agave taste and world-class smoothness. Awarded Silver at the IWSC and ISC in 2020.', NULL, 80, 'Mexico', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (31, 6, 'Patron', '00% Blue Agave. Crystal clear, pure ultra premium. Light, fresh tequila is a favorite of connoisseurs worldwide. Smooth, soft, light tequila. The perfect ingredient in special margaritas or mixed cocktails, neat or on the rocks.', NULL, 80, 'Mexico', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (32, 7, 'Glenlivet Founder\'s Reserve', 'Founder\'s Reserve skillful selection of casks enhances the signature fruity flavors of Glenlivet. Traditional oak casks complemented by selective use of American first-fill casks to give a hint of creamy sweetness for an exceptionally well-balanced and smooth malt.', NULL, 80, 'Scotland', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (33, 7, 'Macallan 12 Year Double Cask', 'The Macallan Double Cask 12 yr. has been crafted to deliver a new style of Macallan. One that is the perfect balance of 100% Sherry seasoned American and European Oak, a great marriage of 2 wood types. Smooth, Sweet and balanced.', NULL, 86, 'Scotland', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (34, 7, 'Dewars', 'A Scotch of unusual subtlety and refinement. Up to 40 of the finest malt and grain whiskies are blended together in perfect harmony, to create this time honored blend. Notes of Scottish heather and honey linger on the finish, with the faintest touch of smoke.', NULL, 80, 'Scotland', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (35, 7, 'Johnnie Walker Black', 'The original Walker family blend, handcrafted from as many as 40 of the finest Scotch whiskies aged a minimum of 12 years, for a smooth and robust blend. Rich smoky malt, peat and sherry fruit character deliver a satisfyingly complex flavor on the long, lingering finish.', NULL, 80, 'Scotland', NULL);
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (36, 1, 'Dad\'s Hat Rye', 'Our classic rye recipe features only rye grain and malt to deliver a pure expression of Pennsylvania Rye Whiskey. Barrel aging for least six months in charred, new oak quarter casks allows the genuine rye flavor to evolve quickly. As a result, the small batch whiskey is ensured of reaching the right balance of complexity and smoothness. The final product is a very smooth spirit that delivers the up-front spice that rye is known for while finishing with a full, round mouthfeel.', NULL, 80, 'United States', 'Pennsylvania');
INSERT INTO `spirit` (`id`, `spirit_type_id`, `name`, `description`, `image_url`, `proof`, `origin_country`, `origin_state_province`) VALUES (37, 1, 'McLaughlin Devil\'s Juice #5', 'You have never tasted anything like our Devil\'s Juice #5. It starts with our artisan whiskey aged in a used bourbon barrel and flavored with toasted hickory wood. It\'s flavor intensity is off the charts and hits you like a punch in the face. If bourbon is a 7 out of 10 on a flavor intensity scale, Devil\'s Juice #5 is a 28. Not for the faint hearted but it is a favorite of seasoned whiskey drinkers. Distilled from grain.', NULL, 120, 'United States', 'Pennsylvania');

COMMIT;


